mapper.select:
SELECT be.eval_id AS evalId, be.eval_type AS evalType, CASE be.eval_type WHEN 'FALL' THEN '낙상위험도 평가' WHEN 'BEDSORE' THEN '욕창위험도 평가' WHEN 'COGNITIVE' THEN '인지기능 평가' WHEN 'NEEDS' THEN '욕구사정 평가' ELSE be.eval_type END AS evalTypeName, be.eval_date AS evalDate, be.beneficiary_id AS beneficiaryId, b.name AS beneficiaryName, COALESCE(mcl.level, '등급없음') AS careLevel, CASE be.eval_type WHEN 'FALL' THEN JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.result.grade')) WHEN 'BEDSORE' THEN JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.grade')) WHEN 'COGNITIVE' THEN JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.interpretation.result')) WHEN 'NEEDS' THEN '평가완료' ELSE JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.grade')) END AS resultGrade, CASE be.eval_type WHEN 'FALL' THEN JSON_EXTRACT(be.eval_data, '$.result.total_score') WHEN 'NEEDS' THEN NULL ELSE JSON_EXTRACT(be.eval_data, '$.total_score') END AS totalScore FROM basic_evaluations be LEFT JOIN beneficiary b ON be.beneficiary_id = b.id LEFT JOIN ( SELECT beneficiary_id, id FROM beneficiary_care_level bcl1 WHERE start_date = ( SELECT MAX(start_date) FROM beneficiary_care_level bcl2 WHERE bcl2.beneficiary_id = bcl1.beneficiary_id ) ) bcl ON b.id = bcl.beneficiary_id LEFT JOIN beneficiary_count bc ON bcl.id = bc.beneficiary_care_level_id LEFT JOIN m_care_level mcl ON bc.m_care_level_id = mcl.id WHERE be.care_worker_id = #{caregiverId} AND be.eval_type = #{evalType}
AND YEAR(be.eval_date) = #{year}
ORDER BY be.eval_date DESC, be.created_at DESC

SELECT be.eval_id AS evalId, be.eval_type AS evalType, CASE be.eval_type WHEN 'FALL' THEN '낙상위험도 평가' WHEN 'BEDSORE' THEN '욕창위험도 평가' WHEN 'COGNITIVE' THEN '인지기능 평가' WHEN 'NEEDS' THEN '욕구사정 평가' ELSE be.eval_type END AS evalTypeName, be.eval_date AS evalDate, be.beneficiary_id AS beneficiaryId, b.name AS beneficiaryName, COALESCE(mcl.level, '등급없음') AS careLevel, CASE be.eval_type WHEN 'FALL' THEN JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.result.grade')) WHEN 'BEDSORE' THEN JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.grade')) WHEN 'COGNITIVE' THEN JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.interpretation.result')) WHEN 'NEEDS' THEN '평가완료' ELSE JSON_UNQUOTE(JSON_EXTRACT(be.eval_data, '$.grade')) END AS resultGrade, CASE be.eval_type WHEN 'FALL' THEN JSON_EXTRACT(be.eval_data, '$.result.total_score') WHEN 'NEEDS' THEN NULL ELSE JSON_EXTRACT(be.eval_data, '$.total_score') END AS totalScore FROM basic_evaluations be LEFT JOIN beneficiary b ON be.beneficiary_id = b.id LEFT JOIN ( SELECT beneficiary_id, id FROM beneficiary_care_level bcl1 WHERE start_date = ( SELECT MAX(start_date) FROM beneficiary_care_level bcl2 WHERE bcl2.beneficiary_id = bcl1.beneficiary_id ) ) bcl ON b.id = bcl.beneficiary_id LEFT JOIN beneficiary_count bc ON bcl.id = bc.beneficiary_care_level_id LEFT JOIN m_care_level mcl ON bc.m_care_level_id = mcl.id WHERE be.beneficiary_id = #{beneficiaryId} AND be.eval_type = #{evalType}
AND YEAR(be.eval_date) = #{year}
ORDER BY be.eval_date DESC, be.created_at DESC
