mapper.select:
SELECT ch.id AS counsel_history_id, ch.detail AS detail, DATE_FORMAT(ch.consult_date, '%Y-%m-%d') AS consult_date, DATE_FORMAT(ch.consult_date, '%H:%i') AS consult_time, e.name AS counselor_name, mcc.name AS m_counsel_category_name
FROM counsel_history ch LEFT JOIN employee e ON ch.counselor_id = e.id LEFT JOIN m_counsel_category mcc ON ch.counsel_category_id = mcc.id
AND ch.potential_id = #{customerId}
AND ch.beneficiary_id = #{customerId}
AND mcc.name = #{counselCategoryName}
ORDER BY ch.consult_date DESC LIMIT #{limit} OFFSET #{offset}
